.projects {
  background-color: #efd1e8;
}

/*  Skills
-----------------------------------*/
.skills {
  background-color: antiquewhite;
}

/*  About
-----------------------------------*/
.about {
  background-color: #fb9b33;
}

/*  Contact
-----------------------------------*/
.contact {
  background-color: #c9cdc0;
}

.blink {
  animation: blink 0.5s infinite;
  font-size: 2.5rem;
}

@keyframes blink {
  to {
    opacity: 0;
  }
}

